import React, { useState } from 'react';
import ReactDOM from 'react-dom';

const Statistics = props => <div>{props.value}</div>

const Button = (props) => (
  <button onClick={props.handleClick}>
    {props.text}
  </button>
)


const App = () => {
  // save clicks of each button to own state
  const [good, setGood] = useState(0)
  const [normal, setNormal] = useState(0)
  const [bad, setBad] = useState(0)
  const [total, setTotal] = useState(0)
  const [average, setAverage] = useState(0)

  const handleGoodClick = () => {
    setTotal(total + 1)
    setGood(good + 1)
  }

  const handleNormalClick = () => {
    setTotal(total + 1)
    setNormal(normal + 1)
  }

  const handleBadClick = () => {
    setTotal(total + 1)
    setBad(bad + 1)
  }

  const theAverage = () => {
    setAverage(total / 3)
  }

  return (
    <div>
      <h1>What do you think?</h1>
      <Button handleClick={() => handleGoodClick(good + 1)} text="Good" />
      <Button handleClick={() => handleNormalClick(normal + 1)} text="Normal" />
      <Button handleClick={() => handleBadClick(bad + 1)} text="Bad" />
      <h1>Statistics</h1>
      <Statistics value={'Good ' + good} />
      <Statistics value={'Normal ' + normal} />
      <Statistics value={'Bad ' + bad} />
      <Statistics value={'Total ' + total} />
      <Statistics value={'Average ' + average } />
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'));
