import React, { useState } from 'react';
import ReactDOM from 'react-dom';

const Statistics = props => <div>{props.value}</div>

const Button = (props) => (
  <button onClick={props.handleClick}>
    {props.text}
  </button>
)


const App = () => {
  // save clicks of each button to own state
  const [good, setGood] = useState(0)
  const [normal, setNormal] = useState(0)
  const [bad, setBad] = useState(0)
  const [average, setAverage] = useState(0)
  const total = good + normal + bad;
  const sumGoodBad = good + bad;

  if (total === 0) {
    return (
      <div>
        <h2>statistics</h2>
        <p>No feedback given</p>
      </div>
    );
  }

  const gAverage = () => {
    setAverage(Math.round(((good - bad) / sumGoodBad) * 100) / 100)
  }

  const handleGoodClick = () => {
    setGood(good + 1)
    gAverage()
  }

  const handleNormalClick = () => {
    setNormal(normal + 1)
    gAverage()
  }

  const handleBadClick = () => {
    setBad(bad + 1)
    gAverage()
  }

  const handleQuote = () => {

  }


  return (
    <div>
      <h1>What do you think?</h1>
      <Button handleClick={() => handleGoodClick()} text="Good" />
      <Button handleClick={() => handleNormalClick()} text="Normal" />
      <Button handleClick={() => handleBadClick()} text="Bad" />
      <h2>Statistics</h2>
      <Statistics value={'Good ' + good} />
      <Statistics value={'Normal ' + normal} />
      <Statistics value={'Bad ' + bad} />
      <Statistics value={'Total ' + total} />
      <Statistics value={'Average ' + average} />
      <Button handleClick={() => handleQuote()} text="Next Anecdotes" />
    </div>
  )
}







ReactDOM.render(<App />, document.getElementById('root'));
